</template>

<script>
export default {
  name: 'AudioPlayer',
  components: {},
  props: {
    // 播放列表
    audioList: {
      type: Array,
      default: null,
    },
  },
  data() {
    return {
      audio: null,
      playMode: "list",// 播放模式：单曲循环，顺序播放，循环播放，随机播放
      currentIndex: 0, // 当前播放的音频位置索引
      isPlaying: false, // 音频是否正在播放
      volume: 100, // 音频音量
      currentTime: 0, // 音频当前播放位置
      duration: 0, // 音频长度
    }
  },
  computed: {
    percentage: function () {
      return this.currentTime / this.duration * 100;
    }
  },
  methods: {
    // 随机播放
    random() {
      this.currentIndex = parseInt(Math.random() * this.audioList.length);
    },
    // 调整音量
    onClickVolume(e) {
      this.volume = e.offsetX;
      this.$refs.audio.volume = this.volume / 100;
    },
    //调整进度
    onClickProgress(e) {
      this.currentTime = e.offsetX / e.target.offsetWidth * this.duration;
      this.$refs.audio.currentTime = this.currentTime;
    },
    // 秒转为分:秒
    mm_ss(val) {
      return ('00' + parseInt(val / 60)).slice(-2) + ":" + ('00' + parseInt(val % 60)).slice(-2)
    },
    // 播放
    play() {

      const playHandler = () => {
        this.$refs.audio.play();
        this.$nextTick(() => {
          this.isPlaying = true
          this.$emit('play', this.currentIndex)
        })
      }
      if (this.beforePlay) {
        this.beforePlay((state) => {
          if (state !== false) {
            playHandler()
          }
          return;
        });
      }
      playHandler();
    },
    // 暂停
    pause() {
      this.isPlaying = false;
      this.$refs.audio.pause();
    },
    // 上一首
    playPrev() {
      if (this.playMode === 'random') {
        this.random();
        return;
      }
      this.currentIndex--;
      if (this.currentIndex < 0) {
        this.currentIndex = this.audioList.length - 1;
      }
    },
    // 下一首
    playNext() {
      if (this.playMode === 'random') {
        this.random();
        return;
      }
      this.currentIndex++;
      if (this.currentIndex > this.audioList.length - 1) {

        this.currentIndex = 0;
      }
    },
    // 客户端开始请求数据
    onLoadStart() {
      this.audio = this.$refs.audio;
      this.currentTime = this.$refs.audio.currentTime;
      if (this.isPlaying) {
        this.play();
      }
    },
    // 客户端正在请求数据，缓冲中
    onProgress() {
      this.duration = this.$refs.audio.duration;
    },
    // 歌曲已经载入完全
    onCanPlayThrough() {
      console.log("onCanPlayThrough");
      if (this.autoPlay) {
        this.play();
      }
    },
    // 音频的播放是否已结束
    onEnded() {
      // 单曲循环模式
      if (this.playMode === 'single-loop') {
        this.play();
        return;
      }
      // 列表播放模式
      if (this.playMode === 'list' && this.currentIndex === this.audioList.length - 1) {
        this.pause();
        return;
      }
      this.playNext();
    },
    onTimeUpdate() {
      this.currentTime = this.$refs.audio.currentTime;
      this.$emit("playing", this.currentTime);
    },
    onLoadedmetadata() {
      console.log("onLoadedmetadata");
    },
  }
}
</script>

